mapper.select:
SELECT `file_id`, `parent_id`, `device_id`, `user_id`, `file_url`, `file_name`, `file_type`, `file_size`, `file_upload_date`, `file_upload_time`, `file_delete_date`, `file_delete_time`, `file_uploader_id`, `file_uploader_name`, `file_delete_id`, `file_delete_name` FROM `file` WHERE
data_status = #{dataStatus,jdbcType=INTEGER}
AND
parent_id = #{parentId,jdbcType=VARCHAR}
AND
device_id = #{deviceId,jdbcType=VARCHAR}
AND
user_id = #{userId,jdbcType=VARCHAR}
ORDER BY file_upload_date DESC
